/**
 * RADFish Theme Vite Plugin
 *
 * This plugin provides theming capabilities for RADFish applications:
 * - Reads theme colors from SCSS files (themes/<theme-name>/styles/_colors.scss)
 * - Exposes config values via import.meta.env.RADFISH_* constants
 * - Injects CSS variables into HTML <head>
 * - Transforms index.html with config values (title, meta tags, favicon)
 * - Writes manifest.json after build via closeBundle
 *
 * Usage:
 *   radFishThemePlugin("noaa-theme")                    // Use noaa-theme from themes/noaa-theme/
 *   radFishThemePlugin("noaa-theme", { app: {...} })    // With config overrides (non-color)
 *
 * Theme Structure:
 *   themes/<theme-name>/
 *     assets/              - Theme icons (served in dev, copied on build)
 *     styles/_colors.scss  - SCSS file with color variables (e.g., $primary: #0054a4;)
 */

import fs from "fs";
import path from "path";

/**
 * Parse SCSS content string and extract variable definitions
 * Supports simple variable definitions like: $variable-name: #hex;
 * @param {string} content - SCSS content as a string
 * @returns {Object} Object mapping variable names (without $) to values
 */
function parseScssContent(content) {
  const variables = {};

  // Match SCSS variable definitions: $variable-name: value;
  // Captures: variable name (without $) and value (without semicolon)
  const variableRegex = /^\s*\$([a-zA-Z_][\w-]*)\s*:\s*([^;]+);/gm;

  let match;
  while ((match = variableRegex.exec(content)) !== null) {
    const name = match[1].trim();
    let value = match[2].trim();

    // Remove !default flag if present
    value = value.replace(/\s*!default\s*$/, "").trim();

    // Convert kebab-case to camelCase for config compatibility
    const camelName = name.replace(/-([a-z])/g, (_, letter) =>
      letter.toUpperCase(),
    );
    variables[camelName] = value;
  }

  return variables;
}

/**
 * Parse SCSS file and extract variable definitions
 * Supports simple variable definitions like: $variable-name: #hex;
 * @param {string} filePath - Path to the SCSS file
 * @returns {Object} Object mapping variable names (without $) to values
 */
function parseScssVariables(filePath) {
  if (!fs.existsSync(filePath)) {
    return {};
  }

  const content = fs.readFileSync(filePath, "utf-8");
  return parseScssContent(content);
}


/**
 * Normalize color value (strip quotes if present)
 */
function normalizeColorValue(value) {
  return value.replace(/['"]/g, '');
}


/**
 * Load theme files and extract variables
 * Returns: { uswdsTokens: {}, radfishVars: {} }
 */
function loadThemeFiles(themeDir) {
  const tokensFile = path.join(themeDir, "styles", "theme-tokens.scss");
  const componentsFile = path.join(themeDir, "styles", "theme-components.scss");

  const uswdsTokens = fs.existsSync(tokensFile)
    ? parseScssVariables(tokensFile)
    : {};

  const radfishVars = fs.existsSync(componentsFile)
    ? parseScssVariables(componentsFile)
    : {};

  return { uswdsTokens, radfishVars };
}

/**
 * Generate _uswds-generated.scss from theme variables
 */
function generateUswdsConfig(themeDir, themeName, uswdsTokens) {
  const generatedDir = path.join(themeDir, "styles", ".generated");
  if (!fs.existsSync(generatedDir)) {
    fs.mkdirSync(generatedDir, { recursive: true });
  }
  const outputPath = path.join(generatedDir, "_uswds-generated.scss");

  // Build USWDS @use statement with all token variables
  const uswdsTokensStr = Object.entries(uswdsTokens)
    .map(([key, value]) => {
      // Convert camelCase to kebab-case for USWDS format
      const kebabKey = key.replace(/([A-Z])/g, '-$1').toLowerCase();
      // Convert kebab-case to USWDS format: base-lightest â†’ theme-color-base-lightest
      const uswdsKey = `theme-color-${kebabKey}`;
      const normalizedValue = normalizeColorValue(value);
      return `  $${uswdsKey}: '${normalizedValue}'`;
    })
    .join(',\n');

  const content = `/**
 * AUTO-GENERATED USWDS Configuration
 * Generated by RADFish theme plugin from themes/${themeName}/styles/theme-tokens.scss
 *
 * DO NOT EDIT MANUALLY - changes will be overwritten
 * To customize: Edit theme-tokens.scss
 */

/* ============================================
   USWDS Configuration (from theme-tokens.scss)
   ============================================ */
@use "uswds-core" with (
${uswdsTokensStr}
);

@forward "uswds";
`;

  fs.writeFileSync(outputPath, content);
  console.log("[radfish-theme] Generated USWDS config:", outputPath);
}

/**
 * Generate _theme-components-generated.scss from RADFish component variables
 */
function generateComponentsCSS(themeDir, themeName, radfishVars) {
  const generatedDir = path.join(themeDir, "styles", ".generated");
  if (!fs.existsSync(generatedDir)) {
    fs.mkdirSync(generatedDir, { recursive: true });
  }
  const outputPath = path.join(generatedDir, "_theme-components-generated.scss");

  const css = [];

  // Header styles
  if (radfishVars.headerBackground || radfishVars['header-background']) {
    const bgValue = radfishVars.headerBackground || radfishVars['header-background'];
    const normalizedBg = normalizeColorValue(bgValue);
    // Check if it's a token reference (from theme-tokens) or direct color
    const bgExpression = normalizedBg.match(/^[a-z-]+$/) ? `color('${normalizedBg}')` : `'${normalizedBg}'`;

    css.push(`/* Header Background */
.usa-header.usa-header--basic.header-container,
.usa-header.header-container,
header.usa-header {
  background-color: ${bgExpression} !important;
}

.usa-header .usa-navbar,
.usa-header--basic .usa-navbar,
.usa-nav-container {
  background-color: ${bgExpression} !important;
}`);
  }

  if (radfishVars.headerLogoWidth || radfishVars['header-logo-width']) {
    const width = radfishVars.headerLogoWidth || radfishVars['header-logo-width'];
    css.push(`/* Header Logo Width */
.header-logo {
  width: ${width};
}`);
  }

  if (radfishVars.headerTitleColor || radfishVars['header-title-color']) {
    const color = radfishVars.headerTitleColor || radfishVars['header-title-color'];
    const normalizedColor = normalizeColorValue(color);
    css.push(`/* Header Title Color */
.header-title {
  color: ${normalizedColor};
}`);
  }

  if (radfishVars.headerMenuBtnColor || radfishVars['header-menu-btn-color']) {
    const btnColor = radfishVars.headerMenuBtnColor || radfishVars['header-menu-btn-color'];
    const normalizedBtnColor = normalizeColorValue(btnColor);
    // Check if it's a token reference (from theme-tokens) or direct color
    const btnColorExpression = normalizedBtnColor.match(/^[a-z-]+$/) ? `color('${normalizedBtnColor}')` : `'${normalizedBtnColor}'`;

    css.push(`/* Header Menu Button Color */
.usa-menu-btn {
  background-color: ${btnColorExpression} !important;
  color: white !important;
}`);
  }

  const content = `/**
 * AUTO-GENERATED RADFish Component Styles
 * Generated by RADFish theme plugin from themes/${themeName}/styles/theme-components.scss
 *
 * DO NOT EDIT MANUALLY - changes will be overwritten
 * To customize: Edit theme-components.scss
 */

@use "uswds-core" as *;

${css.join('\n\n')}
`;

  fs.writeFileSync(outputPath, content);
  console.log("[radfish-theme] Generated component CSS:", outputPath);
}

/**
 * Copy directory recursively
 */
function copyDirSync(src, dest) {
  if (!fs.existsSync(dest)) {
    fs.mkdirSync(dest, { recursive: true });
  }
  const entries = fs.readdirSync(src, { withFileTypes: true });
  for (const entry of entries) {
    const srcPath = path.join(src, entry.name);
    const destPath = path.join(dest, entry.name);
    if (entry.isDirectory()) {
      copyDirSync(srcPath, destPath);
    } else {
      fs.copyFileSync(srcPath, destPath);
    }
  }
}

/**
 * Get content type for file extension
 */
function getContentType(ext) {
  const types = {
    ".png": "image/png",
    ".ico": "image/x-icon",
    ".jpg": "image/jpeg",
    ".jpeg": "image/jpeg",
    ".svg": "image/svg+xml",
    ".webp": "image/webp",
  };
  return types[ext] || "application/octet-stream";
}

/**
 * Generate manifest icon array from config
 */
function getManifestIcons(config) {
  return [
    {
      src: "icons/radfish.ico",
      sizes: "512x512 256x256 144x144 64x64 32x32 24x24 16x16",
      type: "image/x-icon",
    },
    {
      src: "icons/radfish-144.ico",
      sizes: "144x144 64x64 32x32 24x24 16x16",
      type: "image/x-icon",
    },
    {
      src: "icons/radfish-144.ico",
      type: "image/icon",
      sizes: "144x144",
      purpose: "any",
    },
    {
      src: "icons/radfish-192.ico",
      type: "image/icon",
      sizes: "192x192",
      purpose: "any",
    },
    {
      src: "icons/radfish-512.ico",
      type: "image/icon",
      sizes: "512x512",
      purpose: "any",
    },
    {
      src: config.icons.pwa.icon144.replace(/^\//, ""),
      type: "image/png",
      sizes: "144x144",
      purpose: "any",
    },
    {
      src: config.icons.pwa.icon144.replace(/^\//, ""),
      type: "image/png",
      sizes: "144x144",
      purpose: "maskable",
    },
    {
      src: config.icons.pwa.icon192.replace(/^\//, ""),
      type: "image/png",
      sizes: "192x192",
      purpose: "maskable",
    },
    {
      src: config.icons.pwa.icon512.replace(/^\//, ""),
      type: "image/png",
      sizes: "512x512",
      purpose: "maskable",
    },
  ];
}

/**
 * Default configuration values (used if radfish.config.js is missing)
 * Exported so vite.config.js can import and use default colors
 */
export function getDefaultConfig() {
  return {
    app: {
      name: "RADFish Application",
      shortName: "RADFish",
      description: "RADFish React App",
    },
    icons: {
      logo: "/icons/radfish.png",
      favicon: "/icons/radfish.ico",
      appleTouchIcon: "/icons/radfish.png",
      pwa: {
        icon144: "/icons/144.png",
        icon192: "/icons/192.png",
        icon512: "/icons/512.png",
      },
    },
    colors: {
      primary: "#0054a4",
      secondary: "#0093d0",
      accent: "#00467f",
      text: "#333",
      error: "#af292e",
      buttonHover: "#0073b6",
      label: "#0054a4",
      borderDark: "#565c65",
      borderLight: "#ddd",
      background: "#f4f4f4",
      headerBackground: "#0054a4",
      warningLight: "#fff3cd",
      warningMedium: "#ffeeba",
      warningDark: "#856404",
    },
    pwa: {
      themeColor: "#0054a4",
      backgroundColor: "#ffffff",
    },
    typography: {
      fontFamily: "Arial Narrow, sans-serif",
    },
  };
}

/**
 * Deep merge two objects (target values override source)
 */
function deepMerge(source, target) {
  const result = { ...source };
  for (const key of Object.keys(target)) {
    if (target[key] && typeof target[key] === "object" && !Array.isArray(target[key])) {
      result[key] = deepMerge(source[key] || {}, target[key]);
    } else {
      result[key] = target[key];
    }
  }
  return result;
}

/**
 * Main Vite plugin for RADFish theming
 * @param {string} themeName - Name of the theme folder in themes/ directory
 * @param {Object} configOverrides - Optional config overrides (colors, app name, etc.)
 */
export function radFishThemePlugin(themeName = "noaa-theme", configOverrides = {}) {
  let config = null;
  let resolvedViteConfig = null;
  let themeDir = null; // Path to themes/<themeName>/ directory

  return {
    name: "vite-plugin-radfish-theme",

    // Load config and return define values
    async config(viteConfig) {
      // Determine root directory
      const root = viteConfig.root || process.cwd();

      // Start with defaults, then merge provided overrides
      config = deepMerge(getDefaultConfig(), configOverrides);

      // Set theme directory based on theme name
      const themeDirPath = path.resolve(root, "themes", themeName);
      if (fs.existsSync(themeDirPath)) {
        themeDir = themeDirPath;
        console.log("[radfish-theme] Using theme:", themeName);

        // Load theme files: theme-tokens.scss and theme-components.scss
        const { uswdsTokens, radfishVars } = loadThemeFiles(themeDirPath);

        if (Object.keys(uswdsTokens).length > 0) {
          // Merge USWDS tokens into config colors for CSS variable injection
          config.colors = deepMerge(config.colors, uswdsTokens);

          // Generate _uswds-generated.scss from tokens
          generateUswdsConfig(themeDirPath, themeName, uswdsTokens);

          // Generate _theme-components-generated.scss from RADFish variables
          generateComponentsCSS(themeDirPath, themeName, radfishVars);

          console.log("[radfish-theme] Loaded theme from:", themeDirPath);
        } else {
          // Fallback: try old _colors.scss for backwards compatibility
          const colorsScssPath = path.join(themeDirPath, "styles", "_colors.scss");
          if (fs.existsSync(colorsScssPath)) {
            const scssColors = parseScssVariables(colorsScssPath);
            config.colors = deepMerge(config.colors, scssColors);
            console.log("[radfish-theme] Loaded colors from:", colorsScssPath);
          }
        }
      } else {
        console.warn(`[radfish-theme] Theme "${themeName}" not found at ${themeDirPath}`);
      }

      // Return define values for import.meta.env.RADFISH_*
      return {
        define: {
          "import.meta.env.RADFISH_APP_NAME": JSON.stringify(config.app.name),
          "import.meta.env.RADFISH_SHORT_NAME": JSON.stringify(
            config.app.shortName,
          ),
          "import.meta.env.RADFISH_DESCRIPTION": JSON.stringify(
            config.app.description,
          ),
          "import.meta.env.RADFISH_LOGO": JSON.stringify(config.icons.logo),
          "import.meta.env.RADFISH_FAVICON": JSON.stringify(
            config.icons.favicon,
          ),
          "import.meta.env.RADFISH_PRIMARY_COLOR": JSON.stringify(
            config.colors.primary,
          ),
          "import.meta.env.RADFISH_SECONDARY_COLOR": JSON.stringify(
            config.colors.secondary,
          ),
          "import.meta.env.RADFISH_THEME_COLOR": JSON.stringify(
            config.pwa.themeColor,
          ),
          "import.meta.env.RADFISH_BG_COLOR": JSON.stringify(
            config.pwa.backgroundColor,
          ),
        },
      };
    },

    // Store resolved config
    configResolved(viteConfig) {
      resolvedViteConfig = viteConfig;
    },

    // Serve theme assets in dev mode and watch SCSS for changes
    configureServer(server) {
      // Serve manifest.json in dev mode
      server.middlewares.use("/manifest.json", (_req, res) => {
        const manifest = {
          short_name: config.app.shortName,
          name: config.app.name,
          description: config.app.description,
          icons: getManifestIcons(config),
          start_url: ".",
          display: "standalone",
          theme_color: config.pwa.themeColor,
          background_color: config.pwa.backgroundColor,
        };
        res.setHeader("Content-Type", "application/json");
        res.end(JSON.stringify(manifest, null, 2));
      });

      // Serve theme assets if theme directory exists
      if (!themeDir) return;

      // Watch theme files for changes and regenerate _uswds-generated.scss
      const themeTokensPath = path.join(themeDir, "styles", "theme-tokens.scss");
      const themeComponentsPath = path.join(themeDir, "styles", "theme-components.scss");
      const themeOverridesPath = path.join(themeDir, "styles", "theme-overrides.scss");

      if (fs.existsSync(themeTokensPath)) {
        server.watcher.add(themeTokensPath);
      }
      if (fs.existsSync(themeComponentsPath)) {
        server.watcher.add(themeComponentsPath);
      }
      if (fs.existsSync(themeOverridesPath)) {
        server.watcher.add(themeOverridesPath);
      }

      server.watcher.on("change", (changedPath) => {
        if (changedPath === themeTokensPath || changedPath === themeComponentsPath || changedPath === themeOverridesPath) {
          console.log("[radfish-theme] Theme file changed, regenerating CSS...");
          const { uswdsTokens, radfishVars } = loadThemeFiles(themeDir);
          generateUswdsConfig(themeDir, themeName, uswdsTokens);
          generateComponentsCSS(themeDir, themeName, radfishVars);
          // Restart server to apply changes
          console.log("[radfish-theme] Restarting server...");
          server.restart();
        }
      });

      const themeAssetsDir = path.join(themeDir, "assets");
      if (!fs.existsSync(themeAssetsDir)) return;

      // Serve /icons/* from themes/<themeName>/assets/ directory
      server.middlewares.use("/icons", (req, res, next) => {
        const filePath = path.join(themeAssetsDir, req.url || "");
        if (fs.existsSync(filePath) && fs.statSync(filePath).isFile()) {
          res.setHeader(
            "Content-Type",
            getContentType(path.extname(filePath)),
          );
          fs.createReadStream(filePath).pipe(res);
        } else {
          next();
        }
      });

      console.log("[radfish-theme] Serving assets from:", themeAssetsDir);
    },

    // Transform index.html - inject CSS variables and update meta tags
    transformIndexHtml(html) {
      if (!config) return html;

      // Generate CSS variables from config
      const cssVariables = `
    <style id="radfish-theme-variables">
      :root {
        --radfish-primary: ${config.colors.primary};
        --radfish-secondary: ${config.colors.secondary};
        --radfish-accent: ${config.colors.accent};
        --radfish-text: ${config.colors.text};
        --radfish-error: ${config.colors.error};
        --radfish-button-hover: ${config.colors.buttonHover};
        --radfish-label: ${config.colors.label};
        --radfish-border-dark: ${config.colors.borderDark};
        --radfish-border-light: ${config.colors.borderLight};
        --radfish-warning-light: ${config.colors.warningLight};
        --radfish-warning-medium: ${config.colors.warningMedium};
        --radfish-warning-dark: ${config.colors.warningDark};
        --radfish-background: ${config.colors.background};
        --radfish-header-bg: ${config.colors.headerBackground};
        --radfish-font-family: ${config.typography.fontFamily};
      }
    </style>`;

      return html
        .replace("</head>", `${cssVariables}\n  </head>`)
        .replace(
          /<title>.*?<\/title>/,
          `<title>${config.app.shortName}</title>`,
        )
        .replace(
          /<meta name="theme-color" content=".*?" \/>/,
          `<meta name="theme-color" content="${config.pwa.themeColor}" />`,
        )
        .replace(
          /<meta name="description" content=".*?" \/>/,
          `<meta name="description" content="${config.app.description}" />`,
        )
        .replace(
          /<link rel="icon" type="image\/x-icon" href=".*?" \/>/,
          `<link rel="icon" type="image/x-icon" href="${config.icons.favicon}" />`,
        )
        .replace(
          /<link rel="apple-touch-icon" href=".*?" \/>/,
          `<link rel="apple-touch-icon" href="${config.icons.appleTouchIcon}" />`,
        );
    },

    // Write manifest.json after build completes
    closeBundle() {
      if (!config || !resolvedViteConfig) return;

      // Only write manifest for build, not serve
      const outDir = resolvedViteConfig.build?.outDir || "dist";
      const outDirPath = path.resolve(resolvedViteConfig.root, outDir);
      const manifestPath = path.resolve(outDirPath, "manifest.json");

      // Ensure output directory exists
      if (!fs.existsSync(outDirPath)) {
        return; // Build hasn't created output dir yet
      }

      // Copy theme assets to dist/icons if using theme directory
      if (themeDir) {
        const themeAssetsDir = path.join(themeDir, "assets");
        const distIconsDir = path.join(outDirPath, "icons");
        if (fs.existsSync(themeAssetsDir)) {
          copyDirSync(themeAssetsDir, distIconsDir);
          console.log("[radfish-theme] Copied theme assets to:", distIconsDir);
        }
      }

      const manifest = {
        short_name: config.app.shortName,
        name: config.app.name,
        description: config.app.description,
        icons: getManifestIcons(config),
        start_url: ".",
        display: "standalone",
        theme_color: config.pwa.themeColor,
        background_color: config.pwa.backgroundColor,
      };

      fs.writeFileSync(manifestPath, JSON.stringify(manifest, null, 2));
      console.log("[radfish-theme] Wrote manifest.json to", manifestPath);
    },
  };
}

/**
 * Generate VitePWA manifest configuration from radfish.config.js
 * Used by VitePWA plugin for dev mode manifest serving
 */
export function getManifestFromConfig(config) {
  return {
    short_name: config.app.shortName,
    name: config.app.name,
    icons: getManifestIcons(config),
    start_url: ".",
    display: "standalone",
    theme_color: config.pwa.themeColor,
    background_color: config.pwa.backgroundColor,
  };
}
